import React, { useState, useEffect, useRef } from "react";
import { Images } from "./Images";

export default function Cards() {
  const [image, setImage] = useState([]);
  const [currentScore, setScore] = useState(0);
  const [topScore, setHigh] = useState(0);
  const [rightWrong, setrightWrong] = useState("");
  const [theid, setId] = useState([]);
  const [curid, setcurID] = useState(0);
  const [status, setStatus] = useState(false);
  const ref = useRef();

  const imageArray = async () => {
    const res = await { Images };
    const copyarray = res.Images;
    setImage(copyarray);
  };

  useEffect(() => {
    imageArray();
  }, []);

  const shuffleFriends = () => {
    const newarr = [...image];
    newarr.slice();
    newarr.sort(() => Math.random() - 0.5);
    setImage(newarr);
  };

  const wtf = () => {
    console.log();
  };

  // console.log(theid);

  return (
    <div className='centerContainer'>
      <p>{theid}</p>
      {image.map(image => (
        <div className='card'>
          {image.id}
          <img
            ref={ref}
            className='cardImage'
            src={image.cardImage}
            alt='hi'
            clicked={image.clicked}
            status={status}
            onClick={() => {
              setId([...theid, image.id]);
              setcurID([image.id]);
              shuffleFriends();
              setStatus(true);
            }}
          />
        </div>
      ))}
    </div>
  );
}
