import React, { useState, useEffect, useRef } from "react";
import { Images } from "./Images";

export default function Cards() {
  const [image, setImage] = useState([]);
  const ref = useRef();

  const imageArray = async () => {
    const res = await { Images };
    const copyarray = res.Images;
    setImage(copyarray);
  };

  useEffect(() => {
    imageArray();
  }, []);

  const game = () => {
    const newarr = [...image];
    newarr.slice();
    newarr.sort(() => Math.random() - 0.5);
    setImage(newarr);
    console.log(image);
  };

  const addAttr = () => {};

  return (
    <div className='centerContainer'>
      {image.map(image => (
        <div className='card'>
          <img
            ref={ref}
            onClick={() => {
              game();
              addAttr();
            }}
            className='cardImage'
            key={image.navItemID}
            src={image.cardImage}
            alt={image.name}
          />
        </div>
      ))}
      ;
    </div>
  );
}
