import React, { useState, useEffect, useRef, useReducer } from "react";
import { Images } from "./Images";

export default function Cards() {
  const [image, setImage] = useState([]);
  const ref = useRef();

  const imageArray = async () => {
    const res = await { Images };
    const copyarray = res.Images;
    setImage(copyarray);
  };

  useEffect(() => {
    imageArray();
  }, []);

  const game = () => {
    const newarr = [...image];
    newarr.slice();
    newarr.sort(() => Math.random() - 0.5);
    setImage(newarr);
  };

  const [isselected, setSelected] = useState("0");

  // const runme = () => {
  //   let fun = ref.current.classList.add("selected");
  //   console.log(fun);
  //   console.log(ref.current.className);
  // };

  const fuckyeah = () => {
    clearInterval(ref.current);
    console.log(ref);
  };

  const check = () => {
    console.log(ref.current);

    if (ref.current.classList.contains("selected")) {
      console.log("yes");
      fuckyeah();
    } else {
      ref.current.classList.add("selected");
      console.log("no");
    }
  };

  return (
    <div className='centerContainer'>
      {image.map(image => (
        <div
          key={image.navItemId}
          ref={ref}
          className='card'
          onClick={() => {
            game();
            check();
          }}>
          <img className='cardImage' src={image.cardImage} alt={image.name} />
        </div>
      ))}
      ;
    </div>
  );
}
