import React, { useState, useEffect } from "react";
import { Images } from "./Images";

export default function Cards() {
  const [image, setImage] = useState([]);
  const [points, setPoints] = useState(0);

  const game = () => {
    image.sort(() => Math.random() - 0.5);
  };

  const addPoints = () => {};

  const imageArray = async () => {
    const res = await { Images };
    const theImages = res.Images;
    // const copyarray = theImages.slice();
    // copyarray.sort(() => Math.random() - 0.5);
    setImage(copyarray);
  };

  useEffect(() => {
    imageArray();
  }, []);

  return (
    <div className='centerContainer'>
      {image.map(image => (
        <div className='card'>
          <img
            onClick={() => {
              game();
              addPoints();
            }}
            className='cardImage'
            key={image.navItemID}
            src={image.cardImage}
            alt={image.name}
          />
        </div>
      ))}
      ;
    </div>
  );
}
